import Calendar from 'react-calendar';
import React from 'react';
import Switch from "react-switch";


import { useState } from 'react';
import { useToggle } from './utils/useToggle';
import TimeRange from "react-time-range";
import moment from "moment";


import 'react-calendar/dist/Calendar.css';
import './App.css';


function App() {
    const [value, onChange] = useState(new Date());
    const [isOnWeekend, toggleWeekend] = useToggle(true);
    const [isAvailable, toggleAvailable] = useToggle(true);

    state = {
        startTime: "8:00",
        endTime: "22:00"
    };

    const convertToIso = (time) => {
        console.log(typeof time);
        const fullDate = new Date();
        const d = moment(fullDate).format("L");
        return moment(d + " " + time).format();
    };

    const onTimeRangeUpdate = (range) => {
        console.log(range);
        let startTime = "";
        let endTime = "";
        if (range.startTime) {
            startTime = new Date(range.startTime).toLocaleTimeString("en", {
                timeStyle: "short",
                hour12: false
            });
        }
        if (range.endTime) {
            endTime = new Date(range.endTime).toLocaleTimeString("en", {
                timeStyle: "short",
                hour12: false
            });
        }
        console.log(startTime, endTime);
        this.setState({
            startTime,
            endTime
        });
    };
    return (
        <div className="App">
            <div className="page-body">
                <h1>Create Event</h1>

                <div>
                    <input class="event-name" placeholder="Event Name"/>
                </div>

                <br />
                <br />
                <div class='calender-table'>
                    <Calendar
                        onChange={onChange}

                        returnValue={value}


                        calendarType={"US"}

                        selectRange={true}

                        tileDisabled={isOnWeekend ? ({date}) => date.getDay() == 0 || date.getDay() == 6: ({date}) => date.getDay() == null}

                        minDetail={"month"}
                        minDate={new Date()}

                        next2Label={null}
                        prev2Label={null}

                        defaultActiveStartDate={null}
                        activeStartDate={null}

                        defaultValue={null}
                        />
                </div>

                {/*
how to get the start and end dates using values
{value[0] ? value[0].toDateString() : null}
*/}

                <br/>
                <div>
                    <TimeRange
                        startMoment={convertToIso(state.startTime)}
                        endMoment={convertToIso(state.endTime)}
                        onChange={onTimeRangeUpdate}
                        />
                </div>
                <div class="toggle-button-page">

                    <div class="tb1">
                        <div class="tb-button">
                            <label>
                                <Switch onChange={toggleWeekend} checked={isOnWeekend}/>
                            </label>
                        </div>

                        <div class="tb-description">
                            <h3>Weekend</h3>
                            <p>Toggle between enabling and disabling the weekend</p>
                        </div>
                    </div>
                    <br/>
                    <br/>
                    <div class="tb2">
                        <div class="tb-button">
                            <label>
                                <Switch onChange={toggleAvailable} checked={isAvailable}/>
                            </label>
                        </div>
                        <div class="tb-description">
                            <h3>Available</h3>
                            <p>On if choosing selecting avaible time or off to select unavailable time</p>
                        </div>
                    </div>

                </div>

                <br/>
                <br/>
                <br/>
                <div>
                    <button>Create Event</button>
                </div>
            </div>
        </div>
    );
}

export default App;
