import Calendar from 'react-calendar';
import DateRangePicker from '@wojtekmaj/react-daterange-picker'
import React, { Component} from 'react';

import { useState } from 'react';
import { useToggle } from './utils/useToggle';

import moment from "moment";

import 'react-calendar/dist/Calendar.css';
import './App.css';
returnFunctionStart = event => {
    this.setState({ startTime: event.startTime });
};

returnFunctionEnd = event => {
    this.setState({ endTime: event.endTime });
};

function App() {
    const [value, onChange] = useState(new Date());
    const [isOnWeekend, toggleWeekend] = useToggle();
    const state = {
        startTime: moment(),
        endTime: moment()
    };




    return (
        <div className="App">
            <div className='intro'>
                <div class="login-form">
                    <h1>Create Event</h1>
                    <div>
                        <input class="event-name" placeholder="Event Name"/>
                    </div>
                    <br></br>
                    <div>
                        <button onClick={toggleWeekend}>
                            Weekend
                        </button>

                    </div>

                    <form>Add some stuff</form>
                    <div class='calender-table'>
                        <Calendar
                            onChange={onChange}
                            value={value}
                            calendarType={"US"}
                            selectRange={true}
                            tileDisabled={isOnWeekend ? ({date}) => date.getDay() == 0 || date.getDay() == 6: ({date}) => date.getDay() == null}
                            minDetail={"month"}
                            next2Label={null}
                            prev2Label={null}
                            />
                    </div>
                    <div>
                        <DateRangePicker
                            onChange={onChange}
                            tileDisabled={isOnWeekend ? ({date}) => date.getDay() == 0 || date.getDay() == 6: ({date}) => date.getDay() == null}
                            calendarType={"US"}
                            minDetail={"month"}
                            next2Label={null}
                            prev2Label={null}
                            value={value} />
                    </div>
                    <TimeRange
                        onStartTimeChange={this.returnFunctionStart}
                        onEndTimeChange={this.returnFunctionEnd}
                        startMoment={this.state.startTime}
                        endMoment={this.state.endTime}
                        />
                    <div>
                        <p>Availability</p>
                        <input type="checkbox" />
                    </div>
                    <br/>
                    <div>
                        <button>Create Event</button>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default App;
